import { useDispatch, useSelector } from "react-redux";
import updateImg from "../assets/updated.png";
import { useState } from "react";
const TodoItem = () => {
  const todos = useSelector((state) => state.todos);
  const dispatch = useDispatch();

  const [edit, setEdit] = useState(true);
  const handleEditState = () => {
    setEdit(!edit);
  };
  return (
    <div className="mt-[80px] flex justify-center items-center">
      <div className="w-[800px] bg-gray-700 h-[70px] rounded-[0.8rem] ">
        <h2 className="w-[590px] p-[11px]">
          Lorem ipsum dolor sit amet, consectetur adipisicing elit. Ex deserunt
          necessitatibus architecto,
        </h2>

        <div className="flex justify-end items-center px-4 2-[250px] ml-[551px] mt-[-60px]">
          <button className="h-[50px] w-[110px] rounded-[0.8rem] text-white ml-[20px] bg-customBlue">
            ❌
          </button>
          <button
            className="h-[50px] w-[110px] rounded-[0.8rem] text-white ml-[20px] bg-customBlue"
            onClick={handleEditState}
          >
            {edit ? (
              <img className="w-[36px] ml-[27px]" src={updateImg} alt="" />
            ) : (
              <span>✅</span>
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

export default TodoItem;
